import { Button, Space, Modal, Input, Form, Select, Image } from "antd";
import { useEffect, useRef } from "react";
import ReactQuill from "react-quill";
import { useStore } from "../../../../app/stores/store";
import { observer } from "mobx-react-lite";
const ContentModal = () => {
  const { contentStore, articleStore, categoryStore } = useStore();
  const {
    open,
    handleCloseModal,
    formValues,
    handleFinish,
    description,
    setDescription,
    filePreview,
    handleClearImage,
    handleChangeFile,
    loading,
  } = contentStore;

  const [formCat] = Form.useForm();

  const modules = {
    toolbar: [
      [{ header: [1, 2, false] }],
      ["bold", "italic", "underline", "strike", "blockquote"],
      [
        { list: "ordered" },
        { list: "bullet" },
        { indent: "-1" },
        { indent: "+1" },
      ],
      ["link", "image"],
      ["clean"],
    ],
  };

  const formats = [
    "header",
    "bold",
    "italic",
    "underline",
    "strike",
    "blockquote",
    "list",
    "bullet",
    "indent",
    "link",
    "image",
  ];

  const fileRef = useRef(null);

  useEffect(() => {
    articleStore.articleList();
    categoryStore.getList();
  }, [articleStore, categoryStore]);

  useEffect(() => {
    formCat.resetFields();
    formCat.setFieldsValue(formValues);
  }, [formValues, formCat]);

  return (
    <div>
      <Modal
        forceRender
        title={formValues.id == null ? "New Content" : "Update Content"}
        open={open}
        onCancel={handleCloseModal}
        footer={null}
      >
        <Form form={formCat} layout="vertical" onFinish={handleFinish}>
          <Form.Item
            label="Title"
            name={"title"}
            rules={[
              {
                required: true,
                message: "Please input title!",
              },
            ]}
          >
            <Input placeholder="title" />
          </Form.Item>

          {/* <Form.Item name="category" id="category" label="category">
            <Select
              placeholder="Select CategoryName"
              showSearch
              optionFilterProp="label"
              onChange={(value) => {
                articleStore.getArticlesByCategoryId(value);
              }}
            >
              {categoryStore.categories ? (
                categoryStore.categories.map((item, index) => (
                  <Select.Option
                    initialValues=""
                    label={item.nameEn}
                    key={index}
                    value={item.id}
                  >
                    {item.nameEn}
                  </Select.Option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Form.Item> */}

          <Form.Item
            label="ArticleName"
            name={"article"}
            rules={[
              {
                required: true,
                message: "Please Select Article!",
              },
            ]}
          >
            <Select
              placeholder="Select Article"
              showSearch
              optionFilterProp="label"
              // disabled={articleStore.disable}
            >
              {articleStore.articles ? (
                articleStore.articles.map((item, index) => (
                  <Select.Option label={item.name} key={index} value={item.id}>
                    Article: {item.name} || Category: {item.category.nameEn}
                  </Select.Option>
                ))
              ) : (
                <></>
              )}
            </Select>
          </Form.Item>

          <Form.Item
            label="Description"
            name={"description"}
            rules={[
              {
                required: true,
                message: "Please input description!",
              },
            ]}
          >
            <ReactQuill
              theme="snow"
              value={description}
              onChange={setDescription}
              modules={modules}
              formats={formats}
            />
          </Form.Item>

          {formValues.id != null ? (
            <>
              <Form.Item label="Image" name={"imageUrl"}>
                <Image
                  // src={list.mediaUrl}
                  src={
                    filePreview
                      ? filePreview
                      : "data:image/png;base64,"
                  }
                  className="w-full"
                />
              </Form.Item>
              <input
                ref={fileRef}
                type="file"
                onChange={handleChangeFile}
                src={filePreview}
              />
              <button onClick={handleClearImage}>Clear Image</button>
            </>
          ) : (
            <></>
          )}

          <Form.Item style={{ textAlign: "right" }}>
            <Space>
              <Button onClick={handleCloseModal}>Cancel</Button>
              <Button type="primary" htmlType="submit" loading={loading}>
                {formValues.id == null ? "Save" : "Update"}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default observer(ContentModal);
